import java.sql.*;

try {
    Class.forName("org.hsqldb.jdbcDriver");
    String url = bsh.args[0];
    Connection con = DriverManager.getConnection(url, "sa", "");
    String sql = "SHUTDOWN";
    Statement stmt = con.createStatement();
    stmt.executeUpdate(sql);
    stmt.close();
    print("shutdown " + url + " complete");
} catch (Exception e) {
    e.printStackTrace();
}
